mapper.select:
    select a.*,b.* from YearPrice a left outer join FeeItemYearPrice b on a.FeeYear=b.FeeYear
 	 
	 	
	 	and a.FeeYear=#{feeyear}
	 	
	 	
	 	and b.FeeYear in (select FeeYear from FeeItemYearPrice where ItemNo=#{itemNo})
	 	
	 	
	 	and a.Unitprice >=#{minunitprice}
	 	
	 	
	 	and a.Unitprice <=#{maxunitprice}
	 	
	 	
	 	and a.StartDate >=#{startDate}
	 	
	 	
	 	and a.endDate <=#{endDate}
	 	
 	 
 	limit #{start},#{rows}

    select IFNULL(count(FeeYear),0) from YearPrice
     
	 	
	 	and FeeYear=#{feeyear}
	 	
	 	
	 	and FeeYear in (select FeeYear from FeeItemYearPrice where ItemNo=#{itemNo})
	 	
	 	
	 	and Unitprice >=#{minunitprice}
	 	
	 	
	 	and Unitprice <=#{maxunitprice}
	 	
	 	
	 	and StartDate >=#{startDate}
	 	
	 	
	 	and endDate <=#{endDate}
